import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";

function ChatWindow() {

  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you today?"
  }];

  const [messages,setMessages] = useState(defaultMessage)
  const [input, setInput] = useState("");

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
      if (window.MathJax) {
        window.MathJax.typesetPromise();
      }
      scrollToBottom();
  }, [messages]);

  const handleSend = async (input) => {
    if (input.trim() !== "") {
      // Set user message
      setMessages(prevMessages => [...prevMessages, { role: "user", content: input }]);
      setInput("");

      // Call API & set assistant message
      const newMessage = await getAIMessage(input);
      setMessages(prevMessages => [...prevMessages, newMessage]);
    }
  };

  return (
      <div className="messages-container">
          {messages.map((message, index) => {
            const processedContent = message.content
            .replace(/```[\s\S]*?```/g, '')       // Remove complete code blocks.
            .replace(/```/g, '')                   // Remove any leftover backticks.
            .replace(/^\s*verilog\s*$/gm, '')       // Remove lines that contain only "verilog".
            // .replace(/\\boxed\{/g, "$$\\boxed{")     // Replace \boxed{ with $$\boxed{
            // .replace(/\}$/g, "}$$");                // Replace ending } with }$$.
            return (
              <div key={index} className={`${message.role}-message-container`}>
                {message.content && (
                  <div className={`message ${message.role}-message`}>
                    <div dangerouslySetInnerHTML={{ __html: marked.parse(processedContent).replace(/<p>|<\/p>/g, "") }}></div>
                  </div>
                )}
              </div>
            );
          })}
          <div ref={messagesEndRef} />
          <div className="input-area">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message..."
              onKeyPress={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  handleSend(input);
                  e.preventDefault();
                }
              }}
              rows="3"
            />
            <button className="send-button" onClick={() => handleSend(input)}>
              Send
            </button>
          </div>
      </div>
);
}

export default ChatWindow;
